#include "RecoilRLImage.h"#include <cstdlib>#include <cstdio>using namespace Recoil::RenderLow;Image::Image(const char* filename, bool repeat_h, bool repeat_v, float max_anisotropy) {	texture_id = SOIL_load_OGL_texture(filename, SOIL_LOAD_AUTO, SOIL_CREATE_NEW_ID, SOIL_FLAG_INVERT_Y | SOIL_FLAG_MIPMAPS);    	if (texture_id == 0) {		printf("[RenderLow::Image] Failed to load texture file.\n");		return;	}	glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR_MIPMAP_LINEAR);	glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR_MIPMAP_LINEAR);	glTexParameterf(GL_TEXTURE_2D, GL_TEXTURE_MAX_ANISOTROPY_EXT, max_anisotropy);	glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, repeat_h ? GL_REPEAT : GL_CLAMP);	glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, repeat_v ? GL_REPEAT : GL_CLAMP);}Image::~Image(void) {    glDeleteTextures(1, &texture_id);}void Image::Bind(void) {    glBindTexture(GL_TEXTURE_2D, texture_id);}